package hello;

public class conditional {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		if(a<0)
		System.out.println("a is negative"+a);
		else if(a>0)
			System.out.println("a is positive: "+a);
		else
			System.out.println("a is zero");

	}

}
